import React, { Component } from 'react';
import Artist from './Artist'
import './App.css';
import './Artist.css'

class App extends Component {
  constructor(props){
    super(props)

    this.state ={
      artists: []

}
this.compare = this.compare.bind(this)
}


    // componentWillMount(){
    //   const artists = this.state.artists
    //   console.log(artists)
    //   artists.sort(this.compare)
    //   console.log(artists)
    //   this.setState({artists:{artists}})
    // }
    componentDidMount(){
    const BASE_URL = 'https://api.spotify.com/v1/artists?ids=';
    const ids = '3sgAdiPhuvZrC1Di13TVbO,3jfKIjYYnAfdUW40Uragnd,2bqjifJKTLK1AS9AnGbX0L,0T5UNkhytLQkUMkkAqhDzf,51tJbtLGB90l8QN3lxh0ai,7EvvLZS0GP7XuNlWFFXpFR,6LGwrYUj8PpLICKXrawiZ6,4MqwKMTH54nozQRd9TTUYe,22P7J2QhgUyyBuIf5qVWjJ,0Y2zzjMum7MSOCvI0GNbv5,3x5BNVdaDxbzqEQOxPoQ5F,5LslAOPfRnRSrjBEIy6sa0';
    const URL = BASE_URL+ids;
    //spotify:artist:3sgAdiPhuvZrC1Di13TVbO spotify:artist:3jfKIjYYnAfdUW40Uragnd
    const access_token ='BQCH-ig7zcvr2SCyAHWvyBZjPvbPtLMVQKEBfn7xnzxpKT3N2vXcfj303f67FToPQ5_uFpvrjiJl46sMfz-GdwqnGZZtsPqT98D7j9IvhTd8MKbbALTbnJVjP9mVfSXIgPH1ks0Jyp63kZvAen9AKKoPXePc2_RXWI0qWcic6jH8xsXVgbxuSTZY';

    const myOptions = {
      method: 'GET',
      headers : {
        'Authorization': 'Bearer '+access_token
      },
      mode: 'cors',
      cache: 'default'
    }


    fetch(URL, myOptions)
    .then(res => res.json())
    //.then(bug => console.log(bug))
    .then(artists => artists.artists.sort(this.compare))
    .then(artists => this.setState({artists: artists}))
    .then(() => console.log(this.state.artists))

     }
     compare(a,b){
       const followersA = a.followers.total;
       const followersB = b.followers.total;
       let comparison = 0;
        if(followersA>followersB){
          comparison = 1;
        } else if(followersA<followersB){
          comparison = -1;
        }
        return comparison*-1
     }

  render() {
    let artists;
    if(this.state.artists!==0){
      //artists.artists.sort(this.compare)
      artists = this.state.artists.map((artist,i) =>(
        <Artist
        place ={i}
        name = {artist.name}
        image = {artist.images[artist.images.length-2].url}
        popularity = {artist.popularity}
        followers = {artist.followers.total}
        />
        ))

    }
    return (
      <div className='chart'>
        {artists}
      </div>
    );
  }
}

export default App;
