import React, { Component } from 'react';
import Artist from './Artist'
import './App.css';
import './Artist.css'

class App extends Component {
  constructor(props){
    super(props)

    this.state ={
      artists:[]
    }
  }

    componentDidMount(){
    const BASE_URL = 'https://api.spotify.com/v1/artists?ids=';
    const ids = '0oSGxfWSnnOXhD2fKuz2Gy,3dBVyJ7JuOMt4GE9607Qin,3TVXtAsR1Inumwj472S9r4,3sgAdiPhuvZrC1Di13TVbO,3jfKIjYYnAfdUW40Uragnd';
    const URL = BASE_URL+ids;
    //spotify:artist:3sgAdiPhuvZrC1Di13TVbO spotify:artist:3jfKIjYYnAfdUW40Uragnd
    const access_token ='BQCItsUG__vmvaOR5OZmgCm9JxEZ0ybWWIymeIJ0cHFiPPQbHeXnwJHS33Svbp5zrba4IRyTXKeYGMJWQI5Gq85yhuZa6MRNPIj8JbKQF4bxG9-yOFpQ6TTzeqgWOQ0abIjJLT8Tp-LdbsXleGsjCyIqEpATtWqQdbCpfHBxpN_9JFjX2FK2fetC';

    const myOptions = {
      method: 'GET',
      headers : {
        'Authorization': 'Bearer '+access_token
      },
      mode: 'cors',
      cache: 'default'
    }
    // fetch(URL, myOptions)
    // .then(res => res.json())
    // .then(artists => this.setState({artists: artists.artists}))
    // .then(() => console.log(this.state.artists))
     }

  render() {
    let artists;
    if(this.state.artists!==0){
      artists = this.state.artists.map(artist =>(
        <Artist 
        name = {artist.name}
        image = {artist.images[artist.images.length-1].url}
        popularity = {artist.popularity}
        followers = {artist.followers.total}
        />
        ))
    }
    return (
      <div className='chart'>
        {artists}
      </div>
    );
  }
}

export default App;
