import React, { Component } from 'react';
import Artist from './Artist'
import './App.css';
import './Artist.css';
import ids from './AllLithuanianArtists'

class App extends Component {
  constructor(props){
    super(props)

    this.state ={
      artists: []

}
this.compare = this.compare.bind(this)
}


    // componentWillMount(){
    //   const artists = this.state.artists
    //   console.log(artists)
    //   artists.sort(this.compare)
    //   console.log(artists)
    //   this.setState({artists:{artists}})
    // }
    componentDidMount(){
    const BASE_URL = 'https://api.spotify.com/v1/artists?ids=';
    const URL = BASE_URL+ids;
    //spotify:artist:3sgAdiPhuvZrC1Di13TVbO spotify:artist:3jfKIjYYnAfdUW40Uragnd
    const access_token ='BQBENXa0L0XNV7xKbhD8nP5Kh7orvsGEe46kSa6EsSEVPu74x-X6LV4xKNddMQwMdPVjJ5tzewP7vzldgB01_9avWKx0D-Bk8pnmDV8vJu8-DGgPudb0b33NxKVNRtHeO0w0ivJZYYEclM8blDRkd2ABqHw1IRZ_1CrT5MyvzgGH6UjoqspDiEoW';

    const myOptions = {
      method: 'GET',
      headers : {
        'Authorization': 'Bearer '+access_token
      },
      mode: 'cors',
      cache: 'default'
    }


    fetch(URL, myOptions)
    .then(res => res.json())
    //.then(bug => console.log(bug))
    .then(artists => artists.artists.sort(this.compare))
    .then(artists => this.setState({artists: artists}))
    .then(() => console.log(this.state.artists))

     }
     compare(a,b){
       const followersA = a.followers.total;
       const followersB = b.followers.total;
       let comparison = 0;
        if(followersA>followersB){
          comparison = 1;
        } else if(followersA<followersB){
          comparison = -1;
        }
        return comparison*-1
     }

  render() {
    let artists;
    if(this.state.artists!==0){
      //artists.artists.sort(this.compare)
      artists = this.state.artists.map((artist,i) =>{
        if(artist.popularity<39){
        <Artist
        place ={i+1}
        name = {artist.name}
        image = {artist.images[0].url}
        popularity = {artist.popularity}
        followers = {artist.followers.total}
        />
        } else{
        <Artist
        place ={i+1}
        name = {artist.name}
        image = {artist.images[0].url}
        popularity = {artist.popularity}
        followers = {artist.followers.total}
        lit = {<span></span>}
        />
        }
        })

    }
    return (
      <div className='chart'>
        {artists}
      </div>
    );
  }
}

export default App;
