import React, { Component } from 'react';
import Artist from './Artist'
import './App.css';

class App extends Component {
  constructor(props){
    super(props)

    this.state ={
      artists:[]
    }
  }

    componentDidMount(){
    const BASE_URL = 'https://api.spotify.com/v1/artists?ids=';
    const ids = '0oSGxfWSnnOXhD2fKuz2Gy,3dBVyJ7JuOMt4GE9607Qin,3TVXtAsR1Inumwj472S9r4,3sgAdiPhuvZrC1Di13TVbO,3jfKIjYYnAfdUW40Uragnd';
    const URL = BASE_URL+ids;
    //spotify:artist:3sgAdiPhuvZrC1Di13TVbO spotify:artist:3jfKIjYYnAfdUW40Uragnd
    const access_token ='BQCsaZcKdXPuqBB4QbVLa99Zw1q-AjenZqQheQRasXQ8O8ugULxnDLlbqplHiMBpFQ7Opo-P4D3rZRJIHI61JLM5KKbVzgvpxEjYVdBb7f_6plEFbUpPKiNnTaYIBviZagoZpW-N2hcDyeTQ0SbK-obXwLnmxqbcLU2_y4hApeHtbsx0CQc8iEJY';

    const myOptions = {
      method: 'GET',
      headers : {
        'Authorization': 'Bearer '+access_token
      },
      mode: 'cors',
      cache: 'default'
    }
    fetch(URL, myOptions)
    .then(res => res.json())
    .then(artists => this.setState({artists: artists}))
    //.then(() => console.log(this.state.artists))
    }

  render() {
    //const {artists} =  this.state;
    let allArtists = <div>Loading ...</div>
      if(this.state.artists.length!==0){
        //console.log(artists);
        allArtists = this.state.artists.map(artist=>(
          <Artist name={this.state.artists.name}/>
        ));
      }
    return (
      <div>
        {allArtists}
      </div>
    );
  }
}

export default App;
