import React, { Component } from 'react';
import Artist from './Artist'
import './App.css';
import './Artist.css';
//import ids from './AllLithuanianArtists'

class App extends Component {
  constructor(props){
    super(props)

    this.state ={
      artists: []
      }
this.compare = this.compare.bind(this)
//this.getArtistInfo = this.getArtistInfo.bind(this)
}


    //componentWillMount(){

    //}
    componentDidMount(){



    fetch('/api/todos')
      .then(res=>res.json())
      .then(artists=>artists.artists.sort(this.compare))
      .then(artists=> this.setState({artists:artists}))

      .then(()=>console.log(this.state.artists))

    }

    compare(a,b){
      const followersA = a.followers.total;
      const followersB = b.followers.total;
      let comparison = 0;
        if(followersA>followersB){
          comparison = 1;
        } else if(followersA<followersB){
          comparison = -1;
        }
        return comparison*-1
    }



  render() {
    let artists;
    //console.log(this.state.artists)
    if(this.state.artists.length!==0){
      console.log(this.state.artists)
      artists = this.state.artists.map((artist,i) =>{
        var songs;
        var albumImg;
        if(artist.popularSong.length>1){
           songs = artist.popularSong;
           albumImg = artist.albumImage;
        }
        else{
          songs = artist.popularSong;
          albumImg = artist.albumImage;
        }

        if(artist.popularity<40){
        return(
        <Artist
        place ={i+1}
        name = {artist.name}
        image = {artist.images[0].url}
        popularity = {artist.popularity}
        followers = {artist.followers.total}

        //ADDITIONAL INFO GOES UNDER
         song = {songs}
        image = {albumImg}


        />)
        } else{
        return(
        <Artist
        place ={i+1}
        name = {artist.name}
        image = {artist.images[0].url}
        popularity = {artist.popularity}
        followers = {artist.followers.total}

        //ADDITIONAL INFO GOES UNDER
        song = {songs}


         image = {albumImg}


        lit = {<span><i class="fas fa-fire"></i></span>}
        />)
        }
        })

    }
    return (
      <div className='chart'>
        {artists}
      </div>
    );
  }
}

export default App;
